package week1.day2A;

public class ConvertANegativeNumberToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = -754;
		
		if (number<0) {
			number = number *-1;
			System.out.println(number );
			
		}
		else {
			System.out.println("Given number is postive :" +number);
		}
				
				

	}

}
